    /*
    指定表名 获取主键
     */
    public String getTablePk(String tablename) {
        String pk = null;
        DatabaseMetaData dbmd;
        try {
            dbmd = this.connection.getMetaData();
            ResultSet rs = dbmd.getPrimaryKeys(null, null, tablename);
            if (rs.next()) {
                pk = rs.getString(4);
            }
        } catch (SQLException throwables) {
            throwables.printStackTrace();
        }
        return pk;
    }

    /*
    information_schema.KEY_COLUMN_USAGE表描述了具有约束的键列。
    select column_name from information_schema.key_column_usage where table_name = 'stu'and constraint_name='PRIMARY';
     */
    public String getTablePk1(String tablename) {
        String pk = null;
        String sql = "select column_name from information_schema.key_column_usage where table_name = ? and constraint_name='PRIMARY';";
        try {
            PreparedStatement ps = connection.prepareStatement(sql);
            ps.setString(1, tablename);
            ResultSet rs = ps.executeQuery();
            rs.next();
            pk = rs.getString(1);
        } catch (SQLException throwables) {
            throwables.printStackTrace();
        }

        return pk;
    }

    /*
    ju.insert("stu",hm)
     */
    public int insert(String tablename, Map<String, Object> m) {
        int num = 0;
        StringBuilder n = new StringBuilder();
        StringBuilder v = new StringBuilder();
        for (String k : m.keySet()) {
            v.append(k + ",");
            n.append("?,");
        }
        String sql = String.format("insert into %s(%s)values(%s)", tablename, v.toString().substring(0, v.length() - 1), n.toString().substring(0, n.length() - 1));
        PreparedStatement ps;
        int i = 0;
        try {
            ps = connection.prepareStatement(sql);
            for (Object o : m.values()) {
                ps.setObject(++i, o);
            }
            num = ps.executeUpdate();
            ps.close();
        } catch (SQLException throwables) {
            throwables.printStackTrace();
        }
        return num;
    }

    /*
    ju.add("insert into stu (name,stunum,sex,brith)values(?,null,?,?)","刘帅棚","女","1996-5-2");
     */
    public int add(String sql, Object... parameter) {
        int i = 0;
        int num = 1;
        try {
            PreparedStatement ps = connection.prepareStatement(sql);
            for (Object o : parameter) {
                ps.setObject(num++, o);
            }
            i = ps.executeUpdate();
            ps.close();
        } catch (SQLException throwables) {
            throwables.printStackTrace();
        }
        return i;
    }

