    public int uptate(String tableName, String columnName, Object value, String columnN, Object columnV) {
        int result = 0;
        String sql = String.format("update %s set %s = ? where %s  ?", tableName, columnName, columnN);
        try {
            PreparedStatement ps = connection.prepareStatement(sql);

            ps.setObject(1, value);
            ps.setObject(2, columnV);
            result = ps.executeUpdate();
        } catch (SQLException throwables) {
            throwables.printStackTrace();
        }

        return result;
    }

