    public int uptate(String tableName, String columnN, Object columnV, String... columnNameandvalues) {
        int result = 0;
        int i=0;
        StringBuilder sql = new StringBuilder();
        String[] ss= new String[columnNameandvalues.length];
        sql = sql.append("update "+ tableName+" set ");

        for(String s : columnNameandvalues){
            ss[i]=s;
            if(i%2==0 && i==0){
                sql=sql.append(s+"=?");

            }else if(i%2==0){
                sql=sql.append(","+s+"=?");
            }
            i++;
        }
        sql.append( " where "+columnN+"=?");
        //System.out.println(sql.toString());
        for (int j = 0; j < ss.length; j++) {
            System.out.println(ss[j]+j);
        }
        try {
            PreparedStatement ps = connection.prepareStatement(sql.toString());
            for (int j = 0; j < columnNameandvalues.length; j++) {
                if (j%2!=0){
                    if(j==1){
                        ps.setObject(j, ss[j]);
                        //System.out.println(ss[j]);
                        //System.out.println(1);
                    }else {
                        ps.setObject((j+1)/2, ss[j]);
                        //System.out.println((j+1)/2);
                        //System.out.println(ss[j]);
                    }

                }
            }
            ps.setObject((columnNameandvalues.length)/2+1,columnV);
            //System.out.println((columnNameandvalues.length)/2+1);

            result = ps.executeUpdate();
        } catch (SQLException throwables) {
            throwables.printStackTrace();
        }

        return result;
    }

