    /*
        查询所有 limit 从零开始
     */
    public List<Map<String, Object>> select(String tableName, int stratsum, int endsum) {
        String sql = String.format("select * from %s limit %s,%s", tableName, stratsum, endsum);
        List<Map<String, Object>> list = new ArrayList<>();
        int count = 0;
        try {
            PreparedStatement ps = connection.prepareStatement(sql);
            ResultSet rs = ps.executeQuery();
            ResultSetMetaData psmd = rs.getMetaData();
            count = psmd.getColumnCount();
            //rs.next();
            while (rs.next()) {
                Map<String, Object> hm = new HashMap<>();
                for (int i = 1; i <= count; i++) {
                    hm.put(psmd.getColumnName(i), rs.getObject(i));
                    //System.out.println(hm.size());
                }
                //System.out.println(hm);
                list.add(hm);

            }
            rs.close();
        } catch (SQLException throwables) {
            throwables.printStackTrace();
        }
        return list;

    }

