    /*
    pagesize 一页中有多少行
    pages 第几行
     */
    public  int count(String tableName){
        ResultSet li =null;
        int l=0;
        try {
            PreparedStatement ps = connection.prepareStatement(String.format("select count(*) from %s",tableName));
            li=ps.executeQuery();
            while (li.next()){
                l=li.getInt(1);
            }
        } catch (SQLException throwables) {
            throwables.printStackTrace();
        }
        return l;
    }
    /*
    可以设置一页显示我多少条 第几页
     */
    public List<Map<String, Object>> select(String tableName, int pages,boolean b) {

        int l =count(tableName);

        //数据总数
        size = count(tableName);
        //页数
        pagecount= (int)( l%pagesize==0 ?  l/pagesize : l/pagesize+1);
        if (pages>pagecount) pages=pagecount;
        if (pages<1) pages =1;


        int index = (pages * pageSize) - pageSize;
        //System.out.println(String.format("select %s from %s order by id asc limit %s , %s ",condition,tableName,index,pageSize));
        String sql = String.format("select %s from %s order by %s asc  limit %s , %s ", "*", tableName, getTablePk1(tableName),index, pageSize);
        List<Map<String, Object>> list = new ArrayList<>();//0-5 5条 第一页  5-5 5条 第二页
        //01234 1*（一页)-1 0 1*size-1 4
        // 56789 2*(2ye)+1   2*size-1 9
        // 1011121314 3*3+1  3*5-1
        //1516171819 4*4-1   4*5-1
        int count = 0;                                       // 1*size
        try {
            PreparedStatement ps = connection.prepareStatement(sql);
            //System.out.println(sql);
            ResultSet rs = ps.executeQuery();
            ResultSetMetaData psmd = rs.getMetaData();
            count = psmd.getColumnCount();
            while (rs.next()) {
                Map<String, Object> hm = new HashMap<>();
                for (int i = 1; i <= count; i++) {
                    hm.put(psmd.getColumnName(i), rs.getObject(i));
                }
                list.add(hm);
            }
            rs.close();
        } catch (SQLException throwables) {
            throwables.printStackTrace();
        }
        return list;
    }

