public class StringUtils {

    public String getMaskStr(String str, int start, int end, String mask) {
        StringBuilder s = new StringBuilder(str);
        for (int i = start; i < end; i++) {
            s.replace(i, i + 1, mask);
        }
        return s.toString();
    }

    public String getMaskStrName(String name) {
        String ok = "";
        if (name.isBlank() || name.isEmpty()) {
            ok = "***";
        } else {
            ok = name.length() >= 4 ? getMaskStr(name, 1, 3, "*") : getMaskStr(name, 1, 2, "*");
        }
        return ok;
    }

    public String getMaskStrPhone(String phone) {
        return getMaskStr(phone, 3, 7, "*");
    }

    /**
     * @param file
     * @return
     */
    public String getFileName(String file) {
        String fn = "";
        if (file.indexOf("/") != -1) {
            fn = file.substring(file.lastIndexOf("/") + 1).toLowerCase();
        } else if (file.indexOf("\\") != -1) {
            fn = file.substring(file.lastIndexOf("\\") + 1).toLowerCase();
        } else {
            fn = file;
        }
        return fn;
    }

    public String getFileExt(String file) {
        String ext = "";
        if (file.indexOf(".") == -1) {
            ext = "";
        } else {
            ext = file.substring(file.lastIndexOf(".")).toLowerCase();
        }
        return ext;
    }

    public String getFileBaseName(String file) {
        String fn = getFileName(file);
        if (fn.indexOf(".") != -1) {
            fn = fn.substring(0, fn.lastIndexOf(".")).toLowerCase();
        }
        return fn;
    }

    public String getPath(String file) {
        String p = "./";
        if (file.indexOf("/") != -1) {
            p = file.substring(0, file.lastIndexOf("/") + 1).toLowerCase();
        } else if (file.indexOf("\\") != -1) {
            p = file.substring(0, file.lastIndexOf("\\") + 1).toLowerCase();
        } else {

        }
        return p;
    }

    public String getHash(String password, String account) {
        StringBuilder ok = new StringBuilder(32);
        StringBuilder md5 = new StringBuilder(getMd5(password + account));
        StringBuilder sha = new StringBuilder(getSha(password + account));
        ok.append(md5.substring(0, 5));
        ok.append(sha.substring(0, 5));
        ok.append(md5.substring(15, 20));
        ok.append(sha.substring(15, 20));
        ok.append(md5.substring(25));
        ok.append(sha.substring(35));
        return ok.toString();

    }

    public String getSha(String password) {
        StringBuilder pass = new StringBuilder(40);
        try {
            MessageDigest md = MessageDigest.getInstance("sha1");
            md.update(password.getBytes());
            for (byte b : md.digest()) {
                pass.append(String.format("%02x", b));
            }
        } catch (NoSuchAlgorithmException e) {
            e.printStackTrace();
        }
        return pass.toString();

    }

    public String getMd5(String password) {
        StringBuilder pass = new StringBuilder(32);
        try {
            MessageDigest md = MessageDigest.getInstance("md5");
            md.update(password.getBytes());
            for (byte b : md.digest()) {
                pass.append(String.format("%02x", b));
            }
        } catch (NoSuchAlgorithmException e) {
            e.printStackTrace();
        }
        return pass.toString();

    }

    public int search(String src, String dst) {
        int pos = 0;
        int num = 0;
        while (src.indexOf(dst, pos) != -1) {
            pos = src.indexOf(dst, pos) + dst.length();
            ++num;
        }
        return num;
    }

    /**
     * <p>
     * 功能：实现字符串逆序
     * </p>
     *
     * @param str
     * @return
     */
    public String reverse(String str) {
        StringBuilder s = new StringBuilder(str);
        return s.reverse().toString();
    }

    public String getString() {
        return getString(4);
    }

    public String getString(int length) {
        StringBuilder s = new StringBuilder("abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789");
        StringBuilder str = new StringBuilder(length);
        Random random = new Random();
        for (int i = 0; i < length; i++) {
            str.append(s.charAt(random.nextInt(s.length())));
        }
        return str.toString();
    }

}

package cn.netjic.utils;

import javax.imageio.ImageIO;
import java.awt.*;
import java.awt.image.BufferedImage;
import java.io.FileNotFoundException;
import java.io.FileOutputStream;
import java.io.IOException;
import java.util.Random;

/**
 * <p>Description</p>
 *
 * @Auther: netjic@163.com
 * @Date: 2020/11/28/15:28
 */
public class CheckCode {
    public  String yan() {
        Random r = new Random();

        StringUtil s = new StringUtil();
        String str=s.getString();
        int w= 160;
        int h= 80;
        BufferedImage dst = new BufferedImage(w,h,6);
        Graphics2D g = dst.createGraphics();
        g.setRenderingHint(RenderingHints.KEY_ANTIALIASING,RenderingHints.VALUE_ANTIALIAS_ON);
        g.setRenderingHint(RenderingHints.KEY_STROKE_CONTROL,RenderingHints.VALUE_STROKE_DEFAULT);
        Font fo1 = null;
        try {
            fo1 = Font.createFont(Font.TRUETYPE_FONT,Thread.currentThread().getContextClassLoader().getResourceAsStream("font/c.ttf"));
        } catch (FontFormatException e) {
            e.printStackTrace();
        } catch (IOException e) {
            e.printStackTrace();
        }
        g.setColor(new Color(r.nextInt(256),r.nextInt(256),r.nextInt(256)));
        g.setStroke(new BasicStroke(1.0f));
        double x2= r.nextDouble()*30+10;  //控制X轴出现的位置
        double y2= 0;
        int yy = r.nextInt(40)+10; //控制y轴出现的位置
        int hh = 30; //上下高度的振幅
        int aa = 100; //控制频率
        for ( x2 = 10; x2 <=360 ; x2+=0.1) {
            y2=(yy +hh *Math.sin(x2*Math.PI/aa));
            g.drawLine((int)x2,(int)y2,(int)x2,(int)y2);
        }
        StringBuilder aas=new StringBuilder();
        for(int i=0;i<str.length();i++){
            g.setColor(new Color(r.nextInt(200),r.nextInt(200),r.nextInt(200)));
            Font font = fo1.deriveFont(Font.PLAIN,r.nextInt(20)+50);
            g.setFont(font);
            String sa = String.valueOf(str.charAt(i));
            FontMetrics fo = g.getFontMetrics();
            int h1 = fo.getHeight();
            int w1 = fo.stringWidth(sa);

            int x1 = r.nextInt(20)+i*40;
            int y1 = r.nextInt(80-h1/2)+h1/2;
            aas.append(sa);
            //            System.out.println(sa);

            g.drawString(sa,x1,y1);

        }

        g.dispose();

        try {
            ImageIO.write(dst,"png", new FileOutputStream("f:ji/asd.png"));
        } catch (IOException e) {
            e.printStackTrace();
        }

        return aas.toString();
    }
}

