    public Map<String, Object> Oneselect(String id){

        Connection conn = Jdbcutils.getInstance().getConnection();
        String sql = "select * from newtea where id='"+id+"'";
        System.out.println(sql);
        PreparedStatement psta = null;
        ResultSet rst = null;
        ResultSetMetaData rsmd = null;
        Map<String, Object> mso = new HashMap<>();

        try {
            psta = conn.prepareStatement(sql);
            rst  = psta.executeQuery();
            rsmd= rst.getMetaData();
            while (rst.next()){
                for (int i = 1; i <=rsmd.getColumnCount() ; i++) {
                    mso.put(rsmd.getColumnName(i),rst.getObject(i));
                }
            }
            conn.close();
            rst.close();
            psta.close();
        } catch (SQLException throwables) {
            throwables.printStackTrace();
        }finally {

        }

        return mso;
    }
