            while (rst.next()){
                rs=rst.getInt(1);
            }
            conn.close();
            rst.close();
            psta.close();
        } catch (SQLException throwables) {
            throwables.printStackTrace();
        }
        return rs;
    }

    /**
     * @Description: 分页查询
     * @Param:
     * @return: list
     */
    public List<Map> select(Map<String, Integer> map){
        int page=1;
        int pageSize=2;

        if(map.containsKey("page")){
            page =map.get("page");
        }
        if(map.containsKey("pageSize")){
            pageSize=map.get("pageSize");
        }

        int bagin = (page-1)*2;
        String sql = "select * from newtea order by goodsdate desc limit "+bagin+","+pageSize;

        Connection conn = Jdbcutils.getInstance().getConnection();
        int rs = 0;

        List<Map> list = new ArrayList<>();
        try {
            PreparedStatement psta = conn.prepareStatement(sql);
            ResultSet resultSet = psta.executeQuery(sql);
            ResultSetMetaData rsmd = resultSet.getMetaData();
            int n = rsmd.getColumnCount();
            while (resultSet.next()){
                Map<String, String> mss = new HashMap<>();
                for (int i = 1; i <=n; i++) {
                    mss.put(rsmd.getColumnName(i),resultSet.getString(i));
                }
                list.add(mss);
            }
            conn.close();
            resultSet.close();
            psta.close();
        } catch (SQLException throwables) {
            throwables.printStackTrace();
        }

        return list;
    }
