public Map<String, String> asd(HttpServletRequest req ,HttpServletResponse resp){
        try {
            req.setCharacterEncoding("utf-8");
        } catch (UnsupportedEncodingException e) {
            e.printStackTrace();
        }
        //为基于磁盘的文件项创建工厂
        DiskFileItemFactory factory = new DiskFileItemFactory();
        //配置存储库（以确保使用安全的临时位置）
        ServletContext ServletContext = req.getServletContext();
        File repository = (File) req.getServletContext().getAttribute("javax.servlet.context.tempdir");
        factory.setRepository(repository);
        //创建一个新的文件上传处理程序
        ServletFileUpload upload = new ServletFileUpload(factory);
        //upload.setHeaderEncoding("UTF-8");
        //解析请求
        List<FileItem> items = null;
        try {
            items = upload.parseRequest(req);
        } catch (FileUploadException e) {
            e.printStackTrace();
        }

        Calendar calendar = Calendar.getInstance();
        Date date = calendar.getTime();
        SimpleDateFormat sdf = new SimpleDateFormat("yyyy-MM-dd");


        String path = req.getServletContext().getRealPath("/upload/" + sdf.format(date));

        File file = new File(path);
        if (!file.exists()) {
        } else {
            file.mkdirs();
        }

        HashMap<String, String> hm = new HashMap<>();
        Iterator<FileItem> iter = items.iterator();
        while (iter.hasNext()) {
            FileItem item = iter.next();
            if (item.isFormField()) {
                //是普通的表单
                //字符串名称= item.getFieldName();
                //字符串值= item.getString();
                String value = null;
                try {
                    //处理中文乱码
                    value = item.getString("UTF-8");
                } catch (UnsupportedEncodingException e) {
                    e.printStackTrace();
                }
                String key = item.getFieldName();
                hm.put(key, value);
            } else {
                String ext=null;
                if(item.getSize()<1){
                    hm.put("picurl","");
                }else {
                    String name = item.getName();
                    ext = item.getName().substring(name.lastIndexOf("."));
                    String uuid = UUID.randomUUID().toString();
                    String zpath = path + "/" + uuid + ext;
                    try {
                        item.write(new File(zpath));
                    } catch (Exception e) {
                        e.printStackTrace();
                    }
                    hm.put("picurl", "/upload/" + sdf.format(date)+"/"+uuid+ext);
                }

            }
        }
        return hm;
    }
